import React from 'react'
import { ThemeProvider } from 'styled-components'

const theme = {
    whiteMode: {
        color: {
            black: '#333',
            grey: '#f5f5f8',
            white: '#fff'
        }        
    },
    darkMode: {
        color: {
            black: '#000000',
            grey: '#333',
            white: '#888888'
        }        
    },
    fontSizes: {
        small: '10px',
        regular: '16px',
        large: '32px'
    }
}

const Theme = ({ children }) => (
    <ThemeProvider theme={theme}>
        { children }
    </ThemeProvider>
)

export default Theme
