import React, { useState } from 'react'
import styled from 'styled-components'

const Zumthor = () => {
    const [darkMode, setDarkMode] = useState<boolean>(false)
    console.log(darkMode)

    return (
        <Container isDark={false}>
           <h1>Ol√° mundo</h1> 
           <button onClick={ (e)=>setDarkMode(true) }>Dark mode</button>
        </Container>
    )
}

export default Zumthor

const Container = styled.div`
    width: 100vw;
    min-height: 100vh;    
    background-color: ${({ isDark, theme }) => isDark ? theme.darkMode.color.black : theme.whiteMode.color.black}
`
